CREATE TABLE ubid.Houses (markerid INTEGER AUTO_INCREMENT, address VARCHAR(255), latitude DOUBLE, longitude DOUBLE, lease_range VARCHAR(255), unit_type VARCHAR(255), unit_description VARCHAR(255), beds INTEGER, bathrooms DOUBLE, rent DOUBLE, company VARCHAR(255), electric VARCHAR(8), water VARCHAR(8), heat VARCHAR(8), air VARCHAR(8), parking VARCHAR(8), furnished VARCHAR(8), url VARCHAR(255), PRIMARY KEY (markerid)); 
